import React, { useState } from 'react';
import { v4 as uuidV4 } from 'uuid';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const navigate = useNavigate();

    // Create room states
    const [createRoomId, setCreateRoomId] = useState('');
    const [createUsername, setCreateUsername] = useState('');
    const [createPassword, setCreatePassword] = useState('');
    
    // Join room states
    const [joinRoomId, setJoinRoomId] = useState('');
    const [joinUsername, setJoinUsername] = useState('');
    const [joinPassword, setJoinPassword] = useState('');
    
    const createNewRoom = (e) => {
        e.preventDefault();
        const id = uuidV4();
        setCreateRoomId(id);
        toast.success('Created a new room');
    };

    const joinRoom = () => {
        if (!joinRoomId || !joinUsername || !joinPassword) {
            toast.error('ROOM ID, username, and password are required');
            return;
        }

        // Redirect
        navigate(`/editor/${joinRoomId}`, {
            state: {
                username: joinUsername,
                password: joinPassword
            },
        });
    };

    const enterCreateRoom = () => {
        if (!createRoomId || !createUsername) {
            toast.error('ROOM ID & username is required');
            return;
        }

        // Redirect
        navigate(`/editor/${createRoomId}`, {
            state: {
                username: createUsername,
                password: createPassword
            },
        });
    };

    const handleJoinInputEnter = (e) => {
        if (e.code === 'Enter') {
            joinRoom();
        }
    };

    const handleCreateInputEnter = (e) => {
        if (e.code === 'Enter') {
            enterCreateRoom();
        }
    };

    return (
        <div className="homePageWrapper">
            <div className="formWrapper">
                <img
                    className="homePageLogo"
                    src="/code-sync.png"
                    alt="code-sync-logo"
                />
                
                <div className="roomsContainer">
                    {/* Create Room Section - Left */}
                    <div className="roomSection">
                        <h4 className="sectionLabel">Create New Room</h4>
                        <div className="inputGroup">
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="ROOM ID"
                                value={createRoomId}
                                onChange={(e) => setCreateRoomId(e.target.value)}
                                onKeyUp={handleCreateInputEnter}
                                readOnly
                            />
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="USERNAME"
                                value={createUsername}
                                onChange={(e) => setCreateUsername(e.target.value)}
                                onKeyUp={handleCreateInputEnter}
                            />
                            <input
                                type="password"
                                className="inputBox"
                                placeholder="PASSWORD"
                                value={createPassword}
                                onChange={(e) => setCreatePassword(e.target.value)}
                                onKeyUp={handleCreateInputEnter}
                            />
                            <div className="buttonGroup">
                                <button className="btn createBtn" onClick={createNewRoom}>
                                    Generate ID
                                </button>
                                <button className="btn enterBtn" onClick={enterCreateRoom}>
                                    Enter Room
                                </button>
                            </div>
                        </div>
                    </div>
                    
                    {/* Join Room Section - Right */}
                    <div className="roomSection">
                        <h4 className="sectionLabel">Join Existing Room</h4>
                        <div className="inputGroup">
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="ROOM ID"
                                value={joinRoomId}
                                onChange={(e) => setJoinRoomId(e.target.value)}
                                onKeyUp={handleJoinInputEnter}
                            />
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="USERNAME"
                                value={joinUsername}
                                onChange={(e) => setJoinUsername(e.target.value)}
                                onKeyUp={handleJoinInputEnter}
                            />
                            <input
                                type="password"
                                className="inputBox"
                                placeholder="PASSWORD"
                                value={joinPassword}
                                onChange={(e) => setJoinPassword(e.target.value)}
                                onKeyUp={handleJoinInputEnter}
                            />
                            <button className="btn joinBtn" onClick={joinRoom}>
                                Join
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            <footer>
                <h4>
                    
                </h4>
            </footer>
        </div>
    );
};

export default Home;
