import React, { useState } from 'react';
import { v4 as uuidV4 } from 'uuid';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const navigate = useNavigate();

    // Room state
    const [roomId, setRoomId] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    
    // Toggle between Join and Create sections
    const [activeTab, setActiveTab] = useState('join');

    const createNewRoom = (e) => {
        e.preventDefault();
        const id = uuidV4();
        setRoomId(id);
        toast.success('Created a new room');
    };

    const joinRoom = () => {
        if (!roomId || !username || !password) {
            toast.error('ROOM ID, username & password are required');
            return;
        }

        // Redirect with password
        navigate(`/editor/${roomId}`, {
            state: {
                username,
                password,
            },
        });
    };

    const handleInputEnter = (e) => {
        if (e.code === 'Enter') {
            joinRoom();
        }
    };
    
    return (
        <div className="homePageWrapper">
            <div className="formWrapper">
                <img
                    className="homePageLogo"
                    src="/code-sync.png"
                    alt="code-sync-logo"
                />
                
                {/* Tab Navigation */}
                <div className="tabsWrapper">
                    <button 
                        className={`tab ${activeTab === 'join' ? 'active' : ''}`}
                        onClick={() => setActiveTab('join')}
                    >
                        Join Room
                    </button>
                    <button 
                        className={`tab ${activeTab === 'create' ? 'active' : ''}`}
                        onClick={() => setActiveTab('create')}
                    >
                        Create Room
                    </button>
                </div>
                
                {/* Join Room Section */}
                {activeTab === 'join' && (
                    <>
                        <h4 className="mainLabel">Join an existing room</h4>
                        <div className="inputGroup">
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="ROOM ID"
                                onChange={(e) => setRoomId(e.target.value)}
                                value={roomId}
                                onKeyUp={handleInputEnter}
                            />
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="USERNAME"
                                onChange={(e) => setUsername(e.target.value)}
                                value={username}
                                onKeyUp={handleInputEnter}
                            />
                            <input
                                type="password"
                                className="inputBox"
                                placeholder="PASSWORD"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                onKeyUp={handleInputEnter}
                            />
                            <button className="btn joinBtn" onClick={joinRoom}>
                                Join
                            </button>
                        </div>
                    </>
                )}
                
                {/* Create Room Section */}
                {activeTab === 'create' && (
                    <>
                        <h4 className="mainLabel">Create a new room</h4>
                        <div className="inputGroup">
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="ROOM ID (auto-generated)"
                                value={roomId}
                                readOnly
                            />
                            <input
                                type="text"
                                className="inputBox"
                                placeholder="USERNAME"
                                onChange={(e) => setUsername(e.target.value)}
                                value={username}
                            />
                            <input
                                type="password"
                                className="inputBox"
                                placeholder="SET PASSWORD"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                            />
                            <div className="btnGroup">
                                <button className="btn createBtn" onClick={createNewRoom}>
                                    Generate Room ID
                                </button>
                                <button className="btn joinBtn" onClick={joinRoom}>
                                    Create & Join
                                </button>
                            </div>
                        </div>
                    </>
                )}
            </div>
            <footer>
                <h4>
                    
                </h4>
            </footer>
        </div>
    );
};

export default Home;
